it('should broadcast the request via postMessage', () => {
  const callbackSpy = env.sandbox.spy();
  client.makeRequest('request-type', 'response-type', callbackSpy);
  expect(postMessageStub1).to.be.calledWith(serializeMessage('request-type', 'sentinel-123', {}, '01$internalRuntimeVersion$'));
  expect(postMessageStub2).to.be.calledWith(serializeMessage('request-type', 'sentinel-123', {}, '01$internalRuntimeVersion$'));

  // hostWindow2 is the first to send a reply.
  postAmpMessage({
    type: 'response-type',
    sentinel: 'sentinel-123'
  }, hostWindow2);
  expect(callbackSpy).to.be.calledOnce;
  // since hostWindow2 is now set as host, a message from hostWindow1
  // should not be accepted.
  postAmpMessage({
    type: 'response-type',
    sentinel: 'sentinel-123'
  }, hostWindow1);
  expect(callbackSpy).to.be.calledOnce;
  postAmpMessage({
    type: 'response-type',
    sentinel: 'sentinel-123'
  }, hostWindow2);
  expect(callbackSpy).to.be.calledTwice;
  postAmpMessage({
    type: 'response-type',
    sentinel: 'sentinel-123'
  }, hostWindow1);
  expect(callbackSpy).to.be.calledTwice;
});

