it('should report whether native shadow dom supported', () => {
  const shadowDomV0 = !!Element.prototype.createShadowRoot;
  const shadowDomV1 = !!Element.prototype.attachShadow;
  expect(isShadowDomSupported()).to.equal(shadowDomV0 || shadowDomV1);
});

it('reports NONE when no spec is available', () => {
  win.Element.prototype.createShadowRoot = undefined;
  win.Element.prototype.attachShadow = undefined;
  expect(getShadowDomSupportedVersion(win.Element)).to.equal(ShadowDomVersion_Enum.NONE);
});

it('gives preference to v1 over v0 when both specs are available', () => {
  if (!!win.Element.prototype.createShadowRoot && !!win.Element.prototype.attachShadow) {
    expect(getShadowDomSupportedVersion(win.Element)).to.equal(ShadowDomVersion_Enum.V1);
  }
});

it('reports v0 when available but v1 is not', () => {
  if (!!win.Element.prototype.createShadowRoot) {
    win.Element.prototype.attachShadow = undefined;
    expect(getShadowDomSupportedVersion(win.Element)).to.equal(ShadowDomVersion_Enum.V0);
  }
});

it('should report whether native shadow css supported', () => {
  const shadowDomV0 = !!win.Element.prototype.createShadowRoot;
  const shadowDomV1 = !!win.Element.prototype.attachShadow;
  expect(isShadowCssSupported()).to.equal(shadowDomV0 || shadowDomV1);
});

it('should report whether native shadow css supported using proxied Elements', () => {
  const shadowDomV0 = !!win.Element.prototype.createShadowRoot;
  const shadowDomV1 = !!win.Element.prototype.attachShadow;
  if (shadowDomV0) {
    const {
      createShadowRoot
    } = win.Element.prototype;
    win.Element.prototype.createShadowRoot = (...args) => createShadowRoot(...args);
  }
  if (shadowDomV1) {
    const {
      attachShadow
    } = win.Element.prototype;
    win.Element.prototype.attachShadow = (...args) => attachShadow(...args);
  }
  expect(isShadowCssSupported()).to.equal(shadowDomV0 || shadowDomV1);
});

it('should start observing', () => {
  expect(resizeObserverStub.isObserved(element)).to.be.false;
  helper.updatePlaying(true);
  expect(resizeObserverStub.isObserved(element)).to.be.true;
  resizeObserverStub.notifySync({
    target: element,
    borderBoxSize: [{
      inlineSize: 101,
      blockSize: 102
    }]
  });
  expect(element.pause).to.not.be.called;
});

it('should pause only after the element receives a non-zero size', () => {
  expect(resizeObserverStub.isObserved(element)).to.be.false;
  helper.updatePlaying(true);
  expect(resizeObserverStub.isObserved(element)).to.be.true;

  // No size, but didn't have size before.
  resizeObserverStub.notifySync({
    target: element,
    borderBoxSize: [{
      inlineSize: 0,
      blockSize: 0
    }]
  });
  expect(element.pause).to.not.be.called;

  // Has size.
  resizeObserverStub.notifySync({
    target: element,
    borderBoxSize: [{
      inlineSize: 1,
      blockSize: 2
    }]
  });
  expect(element.pause).to.not.be.called;

  // No size, and had size before.
  resizeObserverStub.notifySync({
    target: element,
    borderBoxSize: [{
      inlineSize: 0,
      blockSize: 0
    }]
  });
  expect(element.pause).to.be.calledOnce;
});

it('should unobserve', () => {
  helper.updatePlaying(true);
  expect(resizeObserverStub.isObserved(element)).to.be.true;
  helper.updatePlaying(false);
  expect(resizeObserverStub.isObserved(element)).to.be.false;
});

