it('should backoff exponentially', () => {
  let count = 0;
  const backoff = exponentialBackoff();
  const backoff2 = exponentialBackoff();
  const increment = () => {
    count++;
  };
  backoff(increment);
  expect(count).to.equal(0);
  clock.tick(600);
  expect(count).to.equal(0);
  // Account for jitter
  clock.tick(701);
  expect(count).to.equal(1);

  // Round 2
  backoff(increment);
  expect(count).to.equal(1);
  clock.tick(1200);
  expect(count).to.equal(1);
  clock.tick(1800);
  expect(count).to.equal(2);

  // Round 3
  backoff(increment);
  expect(count).to.equal(2);
  clock.tick(2200);
  expect(count).to.equal(2);
  clock.tick(3200);
  expect(count).to.equal(3);

  // 2nd independent backoff
  backoff2(increment);
  expect(count).to.equal(3);
  clock.tick(600);
  expect(count).to.equal(3);
  clock.tick(701);
  expect(count).to.equal(4);
});

it('should exponentiate correctly', () => {
  const backoff = exponentialBackoffClock();
  const backoff2 = exponentialBackoffClock();

  // base of 2 = 1000 - 300 (30% jitter) = 700
  expect(backoff()).to.equal(700);
  expect(backoff()).to.equal(1400);
  // tick backoff2
  expect(backoff2()).to.equal(700);
  // back to backoff
  expect(backoff()).to.equal(2800);
  expect(backoff()).to.equal(5600);
  expect(backoff()).to.equal(11200);
  expect(backoff()).to.equal(22400);
  expect(backoff2()).to.equal(1400);
  expect(backoff2()).to.equal(2800);
});

it('should work', () => {
  const callback = env.sandbox.spy();
  const throttledCallback = throttle(window, callback, 100);
  throttledCallback(1);
  expect(callback).to.be.calledWith(1); // let 1st call through immediately
  callback.resetHistory();
  clock.tick(20);
  throttledCallback(2);
  clock.tick(20);
  throttledCallback(3);
  clock.tick(20);
  throttledCallback(4);
  clock.tick(20);
  throttledCallback(5);
  clock.tick(19);
  expect(callback).not.to.be.called; // not 100ms yet

  clock.tick(1);
  expect(callback).to.be.calledOnce;
  expect(callback).to.be.calledWith(5);
  callback.resetHistory();
  clock.tick(10);
  throttledCallback(6);
  expect(callback).not.to.be.called;
  clock.tick(89);
  throttledCallback(7, 'another param');
  expect(callback).not.to.be.called;
  clock.tick(1);
  expect(callback).to.be.calledOnce;
  expect(callback).to.be.calledWith(7, 'another param');
});

it('should throttle recursive callback', () => {
  let totalCalls = 0;
  function recursive(countdown) {
    totalCalls++;
    if (countdown > 0) {
      throttledCallback(countdown - 1);
    }
  }
  const throttledCallback = throttle(window, recursive, 100);

  // recursive 3 times
  throttledCallback(3);
  // should immediately invoke callback only once.
  expect(totalCalls).to.equal(1);
  // 2nd invocation happen after the min interval
  clock.tick(100);
  expect(totalCalls).to.equal(2);
  // 3rd invocation
  clock.tick(100);
  expect(totalCalls).to.equal(3);
});

it('should wait before calling', () => {
  const callback = env.sandbox.spy();
  const debounced = debounce(window, callback, 100);
  debounced(1);
  expect(callback).to.not.have.been.called;
  clock.tick(100);
  expect(callback).to.have.been.calledWith(1);
  callback.resetHistory();
  debounced(1);
  expect(callback).to.not.have.been.called;
  debounced(2);
  expect(callback).to.not.have.been.called;
  clock.tick(10);
  debounced(3);
  expect(callback).to.not.have.been.called;
  clock.tick(99);
  expect(callback).to.not.have.been.called;
  clock.tick(1);
  expect(callback).to.have.been.calledWith(3);
});

it('should debounce recursive callback', () => {
  let totalCalls = 0;
  function recursive(countdown) {
    totalCalls++;
    if (countdown > 0) {
      debounced(countdown - 1);
    }
  }
  const debounced = debounce(window, recursive, 100);

  // recursive 3 times
  debounced(2);
  expect(totalCalls).to.equal(0);
  // 1st invocation happen after the min interval
  clock.tick(100);
  expect(totalCalls).to.equal(1);
  // 2nd invocation
  clock.tick(100);
  expect(totalCalls).to.equal(2);
});

