  expect(xyz).to.not.have.been.called;
  expect(onEnqueue2).to.not.have.been.called;
  resolveAbc();
  return macroTask().then(() => {
    expect(abc).calledOnce;
    expect(onEnqueue1).calledOnce;
    expect(xyz).calledOnce;
    expect(onEnqueue2).to.not.have.been.called;
    resolveXyz();
    return macroTask();
  }).then(() => {
    expect(abc).calledOnce;
    expect(onEnqueue1).calledOnce;
    expect(xyz).calledOnce;
    expect(onEnqueue2).calledOnce;
  });
});

