it('should apply desktop aspect ratio based on the active story', async () => {
  const numStories = 2;
  const storyAspectRatios = ['0.75', '0.5'];
  buildStoryPlayer(numStories);
  await manager.loadPlayers();
  await macroTask();

  // Aspect ratio should be applied after the first story is loaded.
  fakeMessaging.sendRequest = () => Promise.resolve({
    value: storyAspectRatios[0]
  });
  const sendRequestSpy = env.sandbox.spy(fakeMessaging, 'sendRequest');
  fireHandler['storyContentLoaded']('storyContentLoaded', {});
  await macroTask();
  expect(sendRequestSpy).to.have.been.calledWith('getDocumentState', {
    'state': 'DESKTOP_ASPECT_RATIO'
  });
  let playerContainer = win.document.querySelector('.i-amphtml-story-player-main-container');
  expect(computedStyle(win, playerContainer).getPropertyValue('--i-amphtml-story-player-panel-ratio').trim()).equal(storyAspectRatios[0]);

  // Aspect ratio should be applied after navigating to the second story and it's loaded.
  fakeMessaging.sendRequest = () => Promise.resolve({
    value: storyAspectRatios[1]
  });
  fireHandler['selectDocument']('selectDocument', {
    next: true
  });
  await macroTask();
  fireHandler['storyContentLoaded']('storyContentLoaded', {});
  await macroTask();
  playerContainer = win.document.querySelector('.i-amphtml-story-player-main-container');
  expect(computedStyle(win, playerContainer).getPropertyValue('--i-amphtml-story-player-panel-ratio').trim()).equal(storyAspectRatios[1]);

  // Aspect ratio should be applied after navigating back to the already loaded first story.
  fireHandler['selectDocument']('selectDocument', {
    previous: true
  });
  await macroTask();
  playerContainer = win.document.querySelector('.i-amphtml-story-player-main-container');
  expect(computedStyle(win, playerContainer).getPropertyValue('--i-amphtml-story-player-panel-ratio').trim()).equal(storyAspectRatios[0]);
});

