  }
  expect(fixedLayer.elements_).to.have.length(5);
  expectFe(fixedLayer.elements_[0], {
    id: 'F0',
    element: element1,
    position: 'fixed',
    selectors: ['#amp-custom-rule1', '#doc-body-id #amp-custom-rule1', '#amp-custom-rule2', '#amp-custom-media-rule1', '#other-rule1']
  });
  expectFe(fixedLayer.elements_[1], {
