it('should ignore additional touches if eventing', () => {
  clock.tick(1);
  let res = recognizer.onTouchStart({
    touches: [{
      clientX: 90,
      clientY: 80
    }, {
      clientX: 110,
      clientY: 120
    }]
  });
  expect(res).to.equal(true);
  res = recognizer.onTouchMove({
    touches: [{
      clientX: 80,
      clientY: 70
    }, {
      clientX: 120,
      clientY: 130
    }]
  });
  expect(res).to.equal(true);

  // On acceptStart, we get a null event
  gesturesMock.expects('signalEmit_').withExactArgs(recognizer, env.sandbox.match(data => {
    return data.centerClientX == 100 && data.centerClientY == 100 && data.deltaX == 10 && data.deltaY == 10 && data.dir == 1 && data.first == true && data.last == false && data.time == 1;
  }), null).once();

  // On onTouchMove, we didn't actually move the original touches
  gesturesMock.expects('signalEmit_').withExactArgs(recognizer, env.sandbox.match(data => {
    return data.centerClientX == 100 && data.centerClientY == 100 && data.deltaX == 10 && data.deltaY == 10 && data.dir == 1 && data.first == false && data.last == false;
  }), {
    touches: [{
      clientX: 80,
      clientY: 70
    }, {
      clientX: 120,
      clientY: 130
    }, {
      clientX: 160,
      clientY: 160
    }]
  }).once();
  recognizer.acceptStart();
  expect(recognizer.eventing_).to.equal(true);

  // Trigger additional touch start; nothing should happen
  // for the additional touch
  res = recognizer.onTouchStart({
    touches: [{
      clientX: 80,
      clientY: 70
    }, {
      clientX: 120,
      clientY: 130
    }, {
      clientX: 160,
      clientY: 160
    }]
  });
  expect(res).to.equal(true);

  // Trigger additional touch move; nothing should happen since the
  // existing touches did not move.
  res = recognizer.onTouchMove({
    touches: [{
      clientX: 80,
      clientY: 70
    }, {
      clientX: 120,
      clientY: 130
    }, {
      clientX: 160,
      clientY: 160
    }]
  });
  expect(res).to.equal(true);

  // Trigger touch end; should not end since two touches are remaining
  res = recognizer.onTouchEnd({
    touches: [{
      clientX: 80,
      clientY: 70
    }, {
      clientX: 120,
      clientY: 130
    }]
  });
  clock.tick(50);
  // Additional touch start and touch move should not trigger a signal
  gesturesMock.expects('signalEnd_').never();
});

