it('should support config processing in draw3pInternal', () => {
  const data = {
    type: 'testAction2'
  };
  const win = {
    context: {
      location: {
        originValidated: true
      },
      data
    }
  };
  let called = false;
  register('testAction2', function (myWin, myData) {
    expect(called).to.be.false;
    called = true;
    expect(myWin).to.equal(win);
    expect(myData).to.not.equal(data);
    expect(myData).to.have.property('custom');
  });
  expect(called).to.be.false;
  let finish;
  draw3pInternal(win, data, (_config, done) => {
    finish = () => {
      done({
        custom: true
      });
    };
  });
  expect(called).to.be.false;
  finish();
  expect(called).to.be.true;
});

