    expect(msrCalls).to.be.calledOnce;
    expect(msrCalls.args[0][1]).to.be.jsonEqual({
      label: 'msr',
      delta: 1
    });
    const timeOriginCall = viewerSendMessageStub.withArgs('tick', env.sandbox.match(arg => arg.label === 'timeOrigin'));
