  yield macroTask();
  expect(spy).to.be.calledWith({
    positionRect: layoutRectLtwh(2, -5, 20, 10),
    relativePos: 'top',
    viewportRect: layoutRectLtwh(0, 0, sizes.width, sizes.height)
  });
