});

it('should prevent XSS via <G> tag and onload attribute', () => {
  const svg = '<svg xmlns="http://www.w3.org/2000/svg">' + '<g onload="javascript:alert(1)"></g></svg>';
  expect(purify(svg)).to.equal('<svg xmlns="http://www.w3.org/2000/svg"><g></g></svg>');
});

it('should prevent XSS via <SCRIPT> tag', () => {
  const svg = '<svg xmlns="http://www.w3.org/2000/svg">' + '<script>alert(1)</script></svg>';
  expect(purify(svg)).to.equal('<svg xmlns="http://www.w3.org/2000/svg"></svg>');
});

it('should prevent simple passive XSS via XLink', () => {
  const svg = '<svg xmlns="http://www.w3.org/2000/svg">' + '<a xmlns:xlink="http://www.w3.org/1999/xlink" ' + 'xlink:href="javascript:alert(1)">' + '<rect width="1000" height="1000" fill="white"/></a></svg>';
  expect(purify(svg)).to.equal('<svg xmlns="http://www.w3.org/2000/svg">' + '<a xmlns:xlink="http://www.w3.org/1999/xlink">' + '<rect fill="white" height="1000" width="1000"></rect></a></svg>');
});

it('should prevent XSS via "from" attribute in SVG and inline-SVG', () => {
  const svg = '<svg>' + '<a xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="?">' + '<circle r="400"></circle>' + '<animate attributeName="xlink:href" begin="0" ' + 'from="javascript:alert(1)" to="&" />' + '</a></svg>';
  expect(purify(svg)).to.equal('<svg><a xlink:href="?" xmlns:xlink="http://www.w3.org/1999/xlink">' + '<circle r="400"></circle></a></svg>');
});

it('should output <use> only if href is relative', () => {
  allowConsoleError(() => {
    const href = '<svg xmlns="http://www.w3.org/2000/svg"><use href="#foo"></use></svg>';
    expect(purify(href)).to.equal(href);
    const xlink = '<svg xmlns="http://www.w3.org/2000/svg"><use xlink:href="#foo"></use></svg>';
    expect(purify(xlink)).to.equal(xlink);
    expect(purify('<svg xmlns="http://www.w3.org/2000/svg"><use href="//evil"></svg>')).to.equal('<svg xmlns="http://www.w3.org/2000/svg"></svg>');
    expect(purify('<svg xmlns="http://www.w3.org/2000/svg"><use xlink:href="//evil"></svg>')).to.equal('<svg xmlns="http://www.w3.org/2000/svg"></svg>');
  });
});

