it('truncates URL', () => {
  let url = 'https://www.example.biz/?';
  for (let i = 0; i < 1000; i++) {
    url += '&23456=8901234567';
  }
  expect(url.length).to.be.above(16384);
  url = truncUrl_(url);
  expect(url.length).to.be.at.most(16384);
  expect(url).to.contain('&__trunc__=1');
});

