it('should maybePumpEarlyFrame and delay extension execution', () => {
  let progress = '';
  const queueExtensions = win.AMP;
  const highPriority = regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += 'high';
  });
  highPriority.p = 'high';
  win.AMP.push(highPriority);
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '1';
  }));
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '2';
  }));
  win.AMP.push(() => {
    progress += 'function';
  });
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '3';
  }));
  expect(queueExtensions).to.have.length(5);
  const promise = adopt(win);
  runChunksForTesting(win.document);
  return promise.then(() => {
    // Skip a microtask.
    return Promise.resolve();
  }).then(() => {
    expect(progress).to.equal('highfunction');
    expect(queueExtensions).to.have.length(3);
    clock.tick();
    expect(queueExtensions).to.have.length(3);
    expect(progress).to.equal('highfunction');
    // New extension arrives before inital ran.
    win.AMP.push(regularExtension(amp => {
      expect(amp).to.equal(win.AMP);
      progress += '4';
    }));
    expect(queueExtensions).to.have.length(4);
    clock.tick(1);
    expect(queueExtensions).to.have.length(0);
    runChunks(win.document);
    return promise;
  }).then(() => {
    expect(progress).to.equal('highfunction1234');
    win.AMP.push(regularExtension(amp => {
      expect(amp).to.equal(win.AMP);
      progress += '5';
    }));
    runChunks(win.document);
    return promise;
  }).then(() => {
    expect(progress).to.equal('highfunction12345');
    expect(queueExtensions).to.have.length(0);
  });
});

it('support struct AMP.push raw functions and high priority', () => {
  // New format: {n:string, f:function()}.
  let progress = '';
  const queueExtensions = win.AMP;

  // Queue mode.
  win.AMP.push(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '1';
  });
  win.AMP.push({
    n: 'ext2',
    ev: '0.1',
    l: true,
    p: 'high',
    f: amp => {
      expect(amp).to.equal(win.AMP);
      progress += 'HIGH';
    },
    v: '$internalRuntimeVersion$'
  });
  expect(queueExtensions).to.have.length(2);
  expect(progress).to.equal('');
  const promise = adopt(win);
  clock.tick(1); // await early frame
  return promise.then(() => {
    // Notice the queue is down to 0 but there is a micro task to execute
    // raw and high prio functions. Also notice that no `runChunksForTesting`
    // was called to process the queue.
    expect(queueExtensions).to.have.length(0);
    expect(progress).to.equal('');
    // Even though raw functions and high priority don't go through chunking
    // there is a micro task for its queue.
    return Promise.resolve();
  }).then(() => {
    expect(queueExtensions).to.have.length(0);
    expect(progress).to.equal('1HIGH');
    win.AMP.push({
      n: 'ext1',
      ev: '0.1',
      l: true,
      f: amp => {
        expect(amp).to.equal(win.AMP);
        progress += 'A';
      },
      v: '$internalRuntimeVersion$'
    });
    runChunks(win.document);
    return promise.then(() => {
      expect(progress).to.equal('1HIGHA');
    });
  });
});

it('should wait for body before processing extensions', function* () {
  let bodyResolver;
  const bodyPromise = new Promise(resolve => {
    bodyResolver = resolve;
  });
  env.sandbox.stub(dom, 'waitForBodyOpenPromise').callsFake(() => bodyPromise);
  function skipMicro() {
    return Promise.resolve().then(() => Promise.resolve());
  }
  function waitNext(promise) {
    return Promise.race([promise, skipMicro()]);
  }
  let progress = '';
  const queueExtensions = win.AMP;
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '1';
  }));
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '2';
  }));
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '3';
  }));
  const promise = adopt(win);
  runChunks(win.document);
  yield waitNext(promise);
  // Extensions are still unprocessed
  expect(progress).to.equal('');

  // Add one more
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '4';
  }));
  runChunks(win.document);
  yield waitNext(promise);
  expect(progress).to.equal('');

  // Body is available now.
  bodyResolver();
  runChunks(win.document);
  yield waitNext(promise);
  expect(progress).to.equal('1234');
  expect(queueExtensions).to.have.length(0);
});

it('should load correct extension version', async function () {
  self.__AMP_MODE = {
    rtvVersion: 'test-version'
  };
  function addExisting(index) {
    const s = document.createElement('script');
    const name = 'amp-test-element' + index;
    s.setAttribute('custom-element', name);
    s.setAttribute('src', `https://cdn.ampproject.org/v0/${name}-0.1.js`);
    win.document.head.appendChild(s);
    return s;
  }
  const s1 = addExisting(1);
  const s2 = addExisting(4);
  const s3 = addExisting(5);
  let bodyResolver;
  const bodyPromise = new Promise(resolve => {
    bodyResolver = resolve;
  });
  env.sandbox.stub(dom, 'waitForBodyOpenPromise').callsFake(() => bodyPromise);
  function skipMicro() {
    return Promise.resolve().then(() => Promise.resolve());
  }
  function waitNext(promise) {
    return Promise.race([promise, skipMicro()]);
  }
  let progress = '';
  const queueExtensions = win.AMP;
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '1';
  }));
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += 'not expected 1';
  }, 'version123'));
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '3';
  }));
  const promise = adopt(win);
  runChunks(win.document);
  await waitNext(promise);
  // Extensions are still unprocessed
  expect(progress).to.equal('');

  // Add one more
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '4';
  }));
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += 'not expected 2';
  }, 'version123'));
  // Add legacy element (5) and eagarly ask for its load as ElementStub does.
  Services.extensionsFor(win).preloadExtension('amp-test-element5', '0.1');
  win.AMP.push(regularExtension(amp => {
    expect(amp).to.equal(win.AMP);
    progress += '5';
  }, 'version123'));
  runChunks(win.document);
  await waitNext(promise);
  expect(progress).to.equal('');

  // Body is available now.
  bodyResolver();
  runChunks(win.document);
  await waitNext(promise);
  expect(progress).to.equal('134');
  expect(queueExtensions).to.have.length(0);
  expect(s1.getAttribute('i-amphtml-loaded-new-version')).to.equal('amp-test-element1');
  expect(s2.getAttribute('i-amphtml-loaded-new-version')).to.equal('amp-test-element4');
  expect(s3.getAttribute('i-amphtml-loaded-new-version')).to.equal('amp-test-element5');
  const inserted = toArray(win.document.head.querySelectorAll('[i-amphtml-inserted]')).map(s => s.src);
  expect(inserted).to.include('https://cdn.ampproject.org/rtv/test-version/v0/amp-test-element1-0.1.js');
  expect(inserted).to.include('https://cdn.ampproject.org/rtv/test-version/v0/amp-test-element4-0.1.js');
  expect(inserted).to.include('https://cdn.ampproject.org/rtv/test-version/v0/amp-test-element5-0.1.js');
});

