it('should be observed and unobserved by the container observer', () => {
  scheduler.schedule(topElement);
  scheduler.schedule(containerElement);
  scheduler.schedule(containerElementChild);
  scheduler.schedule(container);

  // Set container.
  scheduler.setContainer(container);

  // Observed on the document observer.
  expect(intersectionObserverStub.isObserved(topElement, {
    root: doc
  })).to.be.true;
  expect(intersectionObserverStub.isObserved(containerElement, {
    root: doc
  })).to.be.true;
  expect(intersectionObserverStub.isObserved(containerElementChild, {
    root: doc
  })).to.be.true;

  // A contained element is observed by the container.
  expect(intersectionObserverStub.isObserved(containerElement, {
    root: container
  })).to.be.true;
  expect(intersectionObserverStub.isObserved(containerElementChild, {
    root: container
  })).to.be.true;
  // Not observed by the container because not contained by it.
  expect(intersectionObserverStub.isObserved(topElement, {
    root: container
  })).to.be.false;

  // Should not observe the container itself on the container observer.
  expect(intersectionObserverStub.isObserved(container, {
    root: container
  })).to.be.false;
  expect(intersectionObserverStub.isObserved(container, {
    root: doc
  })).to.be.true;

  // Remove container.
  scheduler.removeContainer(container);
  expect(intersectionObserverStub.isObserved(containerElement, {
    root: container
  })).to.be.false;
  expect(intersectionObserverStub.isObserved(containerElementChild, {
    root: container
  })).to.be.false;

  // Set container again.
  scheduler.setContainer(container);
  expect(intersectionObserverStub.isObserved(containerElement, {
    root: container
  })).to.be.true;
  expect(intersectionObserverStub.isObserved(containerElementChild, {
    root: container
  })).to.be.true;
});

