it('promise', () => {
  windowMock.expects('setTimeout').withExactArgs(env.sandbox.match(value => {
    value();
    return true;
  }), 111).returns(1).once();
  let c = 0;
  return timer.promise(111).then(result => {
    c++;
    expect(c).to.equal(1);
    expect(result).to.be.undefined;
  });
});

it('timeoutPromise - no race', () => {
  windowMock.expects('setTimeout').withExactArgs(env.sandbox.match(value => {
    value();
    return true;
  }), 111).returns(1).once();
  let c = 0;
  return timer.timeoutPromise(111).then(result => {
    c++;
    assert.fail('must never be here: ' + result);
  }).catch(reason => {
    c++;
    expect(c).to.equal(1);
    expect(reason.message).to.contain('timeout');
  });
});

it('timeoutPromise - race no timeout', () => {
  windowMock.expects('setTimeout').withExactArgs(env.sandbox.match(unusedValue => {
    // No timeout
    return true;
  }), 111).returns(1).once();
  let c = 0;
  return timer.timeoutPromise(111, Promise.resolve('A')).then(result => {
    c++;
    expect(c).to.equal(1);
    expect(result).to.equal('A');
  });
});

it('timeoutPromise - race with timeout', () => {
  windowMock.expects('setTimeout').withExactArgs(env.sandbox.match(value => {
    // Immediate timeout
    value();
    return true;
  }), 111).returns(1).once();
  let c = 0;
  return timer.timeoutPromise(111, new Promise(() => {})).then(result => {
    c++;
    assert.fail('must never be here: ' + result);
  }).catch(reason => {
    c++;
    expect(c).to.equal(1);
    expect(reason.message).to.contain('timeout');
  });
});

