it('should generate a frame and run callbacks', () => {
  let result = '';
  return new Promise(resolve => {
    vsync.run({
      measure: () => {
        result += 'me1';
      },
      mutate: () => {
        result += 'mu1';
      }
    });
    vsync.run({
      measure: () => {
        result += 'me2';
      },
      mutate: () => {
        result += 'mu2';
      }
    });
    vsync.run({
      measure: () => {
        result += 'me3';
      }
    });
    vsync.run({
      mutate: () => {
        result += 'mu3';
      }
    });
    vsync.mutate(() => {
      result += 'mu4';
      resolve();
    });
    vsync.measure(() => {
      result += 'me4';
      resolve();
    });
  }).then(() => {
    expect(result).to.equal('me1me2me3me4mu1mu2mu3mu4');
  });
});

it('should schedule nested vsyncs', () => {
  let result = '';
  return new Promise(resolve => {
    vsync.run({
      measure: () => {
        result += 'me1';
        vsync.run({
          measure: () => {
            result += 'me2';
          },
          mutate: () => {
            result += 'mu2';
            vsync.run({
              measure: () => {
                result += 'me3';
              }
            });
            vsync.run({
              mutate: () => {
                result += 'mu3';
                resolve();
              }
            });
          }
        });
      },
      mutate: () => {
        result += 'mu1';
      }
    });
  }).then(() => {
    expect(result).to.equal('me1mu1me2mu2me3mu3');
  });
});

