  expect(elementCallback.isEmpty()).to.be.true;
});

it('should respect maxAncestorsToVisit', () => {
  const parent = doc.createElement('div');
  const child = doc.createElement('div');
  parent.appendChild(child);
  parent.id = 'parent';
  let result = false;
  const callback = el => {
    if (el.id == 'parent') {
      result = true;
      return true;
    }
  };
  domAncestorVisitor.addVisitor(callback, 1).visitAncestorsStartingFrom(child);
  expect(result).to.be.false;
  domAncestorVisitor.addVisitor(callback, 2).visitAncestorsStartingFrom(child);
  expect(result).to.be.true;
});

it('should not re-run completed visitors', () => {
