    }
  };
  let result2 = false;
  const callback2 = el => {
    if (el.id == 'parent') {
      result2 = true;
      return true;
    }
  };
  domAncestorVisitor.addVisitor(callback1).visitAncestorsStartingFrom(child);
  expect(result1).to.be.false;
  parent.id = 'parent';
