it('listen', () => {
  const event = getEvent('load', element);
  let c = 0;
  const handler = e => {
    c++;
    expect(e).to.equal(event);
  };
  const unlisten = listen(element, 'load', handler);

  // Not fired yet.
  expect(c).to.equal(0);

  // Fired once.
  loadObservable.fire(event);
  expect(c).to.equal(1);

  // Fired second time.
  loadObservable.fire(event);
  expect(c).to.equal(2);
  unlisten();
  loadObservable.fire(event);
  expect(c).to.equal(2);
});

