test('generator-superset:app:creates files', () => helpers.run(appModule).withPrompts({
  subgenerator: 'package',
  name: 'my-package'
}).then(function () {
  assert.file(['package.json', 'README.md', 'src/index.ts', 'test/index.test.ts']);
}));

