it('returns the rich text of an area via the richText method', function () {
  assert(apos.area.richText({
    metaType: 'area',
    items: [{
      metaType: 'widget',
      type: '@apostrophecms/rich-text',
      content: '<h2>So cool</h2>'
    }, {
      metaType: 'widget',
      type: 'something-else',
      content: '<h3>Do not return me</h3>'
    }, {
      metaType: 'widget',
      type: '@apostrophecms/rich-text',
      content: '<h2>Something else cool</h2>'
    }]
  }) === '<h2>So cool</h2>\n<h2>Something else cool</h2>');
  assert(apos.area.richText({
    metaType: 'area',
    items: [{
      metaType: 'widget',
      type: '@apostrophecms/rich-text',
      content: '<h2>So cool</h2>'
    }, {
      metaType: 'widget',
      type: 'something-else',
      content: '<h3>Do not return me</h3>'
    }, {
      metaType: 'widget',
      type: '@apostrophecms/rich-text',
      content: '<h2>Something else cool</h2>'
    }]
  }, {
    delimiter: ''
  }) === '<h2>So cool</h2><h2>Something else cool</h2>');
  assert(apos.area.richText({
    metaType: 'area',
    items: [{
      metaType: 'widget',
      type: '@apostrophecms/rich-text',
      content: '<h2>So cool</h2>'
    }, {
      metaType: 'widget',
      type: 'something-else',
      content: '<h3>Do not return me</h3>'
    }, {
      metaType: 'widget',
      type: '@apostrophecms/rich-text',
      content: '<h2>Something else cool</h2>'
    }]
  }, {
    wrapper: 'div'
  }) === '<div><h2>So cool</h2></div><div><h2>Something else cool</h2></div>');
});

