it('initial login should produce an incompleteToken, convertible with the afterPasswordVerified requirements', async function () {
  const jar = apos.http.jar(); // establish session

  let page = await apos.http.get('/', {
    jar
  });
  assert(page.match(/logged out/));
  const result = await apos.http.post('/api/v1/@apostrophecms/login/login', {
    method: 'POST',
    body: {
      username: 'HarryPutter',
      password: 'crookshanks',
      session: true,
      requirements: {
        WeakCaptcha: 'xyz'
      }
    },
    jar
  });
  assert(result.incompleteToken); // Make sure it did not create a login session prematurely

  page = await apos.http.get('/', {
    jar
  });
  assert(page.match(/logged out/)); // Make sure it won't convert with an incorrect ExtraSecret

  const token = result.incompleteToken;

  try {
    await apos.http.post('/api/v1/@apostrophecms/login/requirement-verify', {
      body: {
        incompleteToken: token,
        session: true,
        name: 'ExtraSecret',
        value: 'roll-off'
      },
      jar
    });
  } catch ({
    status,
    body
  }) {
    assert(status === 400);
    assert.strictEqual(body.message, 'extra secret incorrect');
    assert.strictEqual(body.data.requirement, 'ExtraSecret');
  } // If we try the final login without
  // having successfully verified all requirements we get an error


  try {
    await apos.http.post('/api/v1/@apostrophecms/login/login', {
      method: 'POST',
      body: {
        incompleteToken: token,
        session: true
      },
      jar
    });
  } catch ({
    status,
    body
  }) {
    assert(status === 403);
    assert.strictEqual(body.message, 'All requirements must be verified');
  } // Make sure it did not create a login session prematurely


  page = await apos.http.get('/', {
    jar
  });
  assert(page.match(/logged out/)); // Fetch props for afterPasswordVerified component

  const props = await apos.http.post('/api/v1/@apostrophecms/login/requirement-props', {
    method: 'POST',
    body: {
      incompleteToken: token,
      name: 'ExtraSecret'
    },
    jar
  });
  assert.strictEqual(props.hint, 'HarryPutter'); // Now convert token to an actual login session
  // by providing the post-password-verification requirements,
  // correctly

  await apos.http.post('/api/v1/@apostrophecms/login/requirement-verify', {
    body: {
      incompleteToken: token,
      session: true,
      name: 'ExtraSecret',
      value: 'roll-on'
    },
    jar
  });
  await apos.http.post('/api/v1/@apostrophecms/login/login', {
    method: 'POST',
    body: {
      incompleteToken: token,
      session: true
    },
    jar
  });
  page = await apos.http.get('/', {
    jar
  });
  assert(page.match(/logged in/));
});

