it('basic reverse relationship query works', async function () {
  let apos;

  try {
    apos = await t.create({
      root: module,
      modules: {
        product: {
          options: {
            alias: 'product'
          },
          extend: '@apostrophecms/piece-type',
          fields: {
            add: {
              _salespeople: {
                type: 'relationship',
                withType: 'salesperson'
              }
            }
          }
        },
        salesperson: {
          options: {
            alias: 'salesperson'
          },
          extend: '@apostrophecms/piece-type',
          fields: {
            add: {
              _products: {
                type: 'relationshipReverse',
                withType: 'product',
                reverseOf: '_salespeople'
              }
