it('should watch and rebuild assets in a debounced queue', async function () {
  await t.destroy(apos);
  let timesRebuilt = 0;
  const inc = () => {
    timesRebuilt += 1;
  };
  apos = await t.create({
    root: module,
    autoBuild: true,
    modules: {
      ...modules,
      '@apostrophecms/asset': {
        extendMethods() {
          return {
            async watchUiAndRebuild(_super) {
              return _super(inc);
            }
          };
        }
      }
    }
  });
  assert(apos.asset.buildWatcher);
  const assetPath = path.join(process.cwd(), 'test/modules/bundle-page/ui/src/extra.js');
  const assetPathPublic = path.join(process.cwd(), 'test/public/apos-frontend/default/extra-module-bundle.js');
  const assetContent = fs.readFileSync(assetPath, 'utf-8');

  // Modify below the debounce rate
  for (const i of [1, 2, 3]) {
    await fs.writeFile(assetPath, `export default () => { 'bundle-page-watcher-test-${i}'; };\n`, 'utf8');
    await Promise.delay(300);
  }
  await retryAssertTrue(async () => (await fs.readFile(assetPathPublic, 'utf8')).match(/bundle-page-watcher-test-3/), 'Unable to verify public asset rebuilding by the watcher', 500, 10000);
  await retryAssertTrue(() => timesRebuilt === 1, `Expected to rebuild 1 time, got ${timesRebuilt}`, 100, 5000);

  // Modify above the debounce rate, test the queue cap
  timesRebuilt = 0;
  for (const i of [1, 2, 3]) {
    await fs.writeFile(assetPath, `export default () => { 'bundle-page-watcher-test-${i}0'; };\n`, 'utf8');
    await Promise.delay(1050);
  }
  await retryAssertTrue(async () => (await fs.readFile(assetPathPublic, 'utf8')).match(/bundle-page-watcher-test-30/), 'Unable to verify public asset rebuilding by the watcher', 500, 10000);
  await retryAssertTrue(() => timesRebuilt === 3, `Expected to rebuild 3 times, got ${timesRebuilt}`, 100, 5000);
  await t.destroy(apos);
  apos = null;
  fs.writeFileSync(assetPath, assetContent, 'utf8');
});

