it('should compile errors', function () {
  const initialState = apos.util.pipe(apos.commandMenu.composeRemoves, apos.commandMenu.composeCommands, apos.commandMenu.composeGroups, apos.commandMenu.composeModals)({
    definitions: [[moduleA.commands], [moduleB.commands], [moduleC.commands], [moduleD.commands], [{
