    }], [{
      group: {
        groupA: {}
      }
    }], [{
      modal: {
        modalA: {
          groupB: {}
        }
      }
    }]]
  });
  const result = [].concat(Object.entries(initialState.commands).map(([name, command]) => apos.commandMenu.validateCommand({
    name,
    command
  })), Object.entries(initialState.groups).map(([name, group]) => apos.commandMenu.validateGroup({
    name,
    group
  })), Object.entries(initialState.modals).flatMap(([name, modal]) => apos.commandMenu.validateModal({
    name,
    modal
  })));
  const actual = () => apos.commandMenu.compileErrors(result);
  const expected = {
    name: 'Error',
    message: 'Invalid',
    cause: [new assert.AssertionError({
      message: 'Invalid command type, must be "item", for command1',
      expected: 'item',
      operator: 'strictEqual'
    }), new assert.AssertionError({
      message: 'Invalid group label, must be a string, for groupA "undefined" provided',
      actual: 'undefined',
      expected: 'string',
      operator: 'strictEqual'
    }), new assert.AssertionError({
      message: 'Invalid group label, must be a string, for modalA:groupB "undefined" provided',
      actual: 'undefined',
      expected: 'string',
      operator: 'strictEqual'
    })]
  };
  assert.throws(actual, expected);
});

