it('should be able to retrieve hobbies in parallel with all relationships', async function () {
  const req = apos.task.getReq();
  const hobbyists = [];
  for (let i = 0; i < 10; i++) {
    hobbyists.push(await apos.person.insert(req, {
      title: `Hobbyist ${i}`,
      visibility: 'public'
    }));
  }
  for (let i = 0; i < 10; i++) {
    await apos.person.update(req, {
      ...hobbyists[i],
      hobbies: [{
        name: `Hobby ${i}`,
        _friends: [
        // Deep clone to avoid infinite recursion during the save operation
        // as 4 points to 5 and vice versa
        klona(hobbyists[9 - i])]
      }]
    });
  }
  const promises = [];
  for (let i = 0; i < 100; i++) {
    const req = apos.task.getReq();
    promises.push(apos.person.find(req).toArray());
  }
  const results = await Promise.all(promises);
  assert.strictEqual(results.length, 100);
  for (const result of results) {
    // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/String/localeCompare
    result.sort((a, b) => a.title.localeCompare(b.title));
    assert.strictEqual(result.length, 10);
    for (let i = 0; i < 10; i++) {
      const person = result[i];
      assert.strictEqual(person.title, `Hobbyist ${i}`);
      assert.strictEqual(person.hobbies.length, 1);
      assert.strictEqual(person.hobbies[0].name, `Hobby ${i}`);
      assert(person.hobbies[0]._friends);
      assert(person.hobbies[0]._friends[0]);
      assert.strictEqual(person.hobbies[0]._friends[0].title, `Hobbyist ${9 - i}`);
    }
  }
});

it('should replicate key docs across locales at startup', async function () {
  apos = await t.create(config);
  const homes = await apos.doc.db.find({
    parkedId: 'home'
  }).toArray();
  // Draft and published
  assert(homes.length === 8);
  const archives = await apos.doc.db.find({
    parkedId: 'archive'
  }).toArray();
  assert(archives.length === 8);
  // Make sure all archive docs have the archived property set `true`
  assert(!archives.find(archive => !archive.archived));
  const globals = await apos.doc.db.find({
    type: '@apostrophecms/global'
  }).toArray();
  assert(globals.length === 8);
  const people = await apos.doc.db.find({
    parkedId: 'people'
  }).toArray();
  assert(people.length === 8);
  // People page in fr-CA has expected parked properties
  const req = apos.task.getReq();
  const peoplePageEn = await apos.page.find(req, {
    parkedId: 'people'
  }).toObject();
  const frCAReq = apos.task.getReq({
    locale: 'fr-CA'
  });
  let peoplePageFrCA = await apos.page.find(frCAReq, {
    parkedId: 'people'
  }).toObject();
  assert(peoplePageEn.aposDocId === peoplePageFrCA.aposDocId);
  assert(peoplePageEn.aposLocale === 'en:published');
  assert(peoplePageFrCA.aposLocale === 'fr-CA:published');
  assert(peoplePageEn.title === 'People');
  assert(peoplePageFrCA.title === 'People');
  peoplePageFrCA.title = 'Altered';
  await apos.page.update(frCAReq, peoplePageFrCA);
  peoplePageFrCA = await apos.page.find(frCAReq, {
    parkedId: 'people'
  }).toObject();
  assert(peoplePageFrCA.title === 'Altered');
});

