  }), {
    status: 400
  });
  await assert.rejects(() => apos.http.post('/api/v1/@apostrophecms/login/reset', {
    body: {
      // explicit check for boolean cheat!
      reset: false,
      email: user.email,
      password: 'new more secret',
      session: true
    },
    jar
  }), {
    status: 400
  });

  // Reset
  await apos.http.post('/api/v1/@apostrophecms/login/reset', {
    body: {
      reset: resetToken,
      email: user.email,
      password: 'new more secret',
      session: true
