it('test modules exist', async function () {
  apos = await t.create({
    root: module,
    modules: {
      cats: {
        extend: '@apostrophecms/piece-type',
        fields: {
          add: {
            flavor: {
              type: 'select',
              label: 'Flavor',
              choices: [{
                label: 'Grape',
                value: 'grape'
              }, {
                label: 'Cherry',
                value: 'cherry'
              }, {
                label: 'Mint',
                value: 'mint'
              }]
            }
          }
        },
        options: {
          name: 'cat',
          label: 'Cat',
          alias: 'cats'
        }
      },
      people: {
        extend: '@apostrophecms/piece-type',
        options: {
          name: 'person',
          label: 'Person',
          alias: 'people'
        },
        fields: {
          add: {
            _cats: {
              type: 'relationship',
              idsStorage: 'catsIds',
              label: 'Cats',
              withType: 'cat'
            },
            _favorites: {
              type: 'relationship',
              max: 1,
              idsStorage: 'favoriteIds',
              label: 'Favorites',
              withType: 'cat'
            },
            isACatPerson: {
              type: 'boolean',
              label: 'Is a cat person?',
              required: true
            }
          }
        }
      }
    }
  });
  assert(apos.schema);
  assert(apos.cats);
  assert(apos.people);
  apos.argv._ = [];
  let i;
  for (i = 0; i < 11; i++) {
    cats[i] = {};
    cats[i].title = 'Cat ' + i;
    cats[i].i = i;
    people[i] = {};
    people[i].i = i;
    people[i].title = 'Person ' + i;
  }
  cats[0].flavor = 'cherry';
  cats[1].flavor = 'mint';
  cats[4].flavor = 'mint';
  const req = apos.task.getReq();
  await apos.doc.db.deleteMany({
    type: 'cat'
  });
  for (i = 0; i < cats.length; i++) {
    cats[i] = await apos.cats.insert(req, cats[i]);
  }
  for (i = 0; i < people.length; i++) {
    const person = people[i];
    person.isACatPerson = true;
    // person 10 has no favorite cat
    if (i < 10) {
      person.favoriteIds = [cats[i].aposDocId];
      person.isACatPerson = false;
    }
    person.catsIds = [];
    let j;
    // person 10 is allergic to cats
    if (i < 10) {
      for (j = 0; j < i; j++) {
        person.catsIds.push(cats[j].aposDocId);
      }
    }
    people[i] = await apos.people.insert(req, person);
  }
});

