it("1. Create queries", function () {
  // writing query to get all schema
  _.dataSources.CreateQueryAfterDSSaved("SELECT schema_name FROM information_schema.schemata;", "get_schema");
  // switching off Use Prepared Statement toggle
  _.dataSources.ToggleUsePreparedStatement(false);
  _.dataSources.RunQuery();

  // writing query to get all the tables
  _.dataSources.CreateQueryFromOverlay(datasourceName, `select * from pg_catalog.pg_tables where schemaname = {{schema_select.selectedOptionValue || "public"}};`, "get_tables", 2000);
  _.dataSources.RunQuery();

  // writing query to get all the columns
  _.dataSources.CreateQueryFromOverlay(datasourceName, `SELECT column_name, data_type, table_name, ordinal_position, is_nullable FROM information_schema.COLUMNS`, "get_columns");
  _.dataSources.RunQuery();

  // writing query to get create table
  _.dataSources.CreateQueryFromOverlay(datasourceName, `CREATE TABLE {{schema_select.selectedOptionValue}}.{{nt_name.text.replaceAll(" ","_")}}({{appsmith.store.nt_col.map((c)=> c.name.replaceAll(" ","_") + " " + c.dtype + (c.nnull ? " NOT NULL " : "") + (c.pkey ? " PRIMARY KEY " : "")).join(" , ")}})`, "create_table");
  _.dataSources.ToggleUsePreparedStatement(false);

  // writing query to get drop table
  _.dataSources.CreateQueryFromOverlay(datasourceName, `DROP TABLE {{schema_select.selectedOptionValue}}.{{nt_name.text.replaceAll(" ","_")}}({{appsmith.store.nt_col.map((c)=>c.name.replaceAll(" ","_") + " " + c.dtype + (c.nnull ? " NOT NULL " :  "") + (c.pkey ? " PRIMARY KEY " : "")).join(" , ")}});`, "drop_table");
});

