        let noPriVal = 1;
        issue.labels.map(label => {
          if (label.name === "Critical") {
            CritLen++;
            noPriVal = 0;
          } else if (label.name === "High") {
            HighLen++;
            noPriVal = 0;
          } else if (label.name === "Low") {
            LowLen++;
            noPriVal = 0;
          }
        });
        NoPriLen += noPriVal;
      });
      storeValue("Bug-" + param.index + "", {
        x: param.label,
        critY: CritLen,
        highY: HighLen,
        lowY: LowLen,
        noPriY: NoPriLen
      });
    }, undefined, {
      label: label,
      index: index
    });
    Query1.run((res, param) => {
      let CritLen = 0;
      let HighLen = 0;
      let LowLen = 0;
      let NoPriLen = 0;
      res.map(issue => {
        let noPriVal = 1;
        issue.labels.map(label => {
          if (label.name === "Critical") {
            CritLen++;
            noPriVal = 0;
          } else if (label.name === "High") {
            HighLen++;
            noPriVal = 0;
          } else if (label.name === "Low") {
            LowLen++;
            noPriVal = 0;
          }
        });
        NoPriLen += noPriVal;
      });
      storeValue("Feature-" + param.index, {
        x: param.label,
        critY: CritLen,
        highY: HighLen,
        lowY: LowLen,
        noPriY: NoPriLen
      });
    }, undefined, {
      label: label,
      index: index
    });
  });}}`;
