          expect(theight).to.not.equal(tnewheight);
          cy.get(".t--widget-containerwidget").eq(0).invoke("css", "height").then(newcheight => {
            expect(oheight).to.not.equal(newcheight);
            cy.moveToContentTab();
            const modifierKey = Cypress.platform === "darwin" ? "meta" : "ctrl";
            cy.get(".CodeMirror textarea").first().focus().type(`{${modifierKey}}a`).then($cm => {
