    cy.generateUUID().then(uid => {
      const owner = Cypress.env("TEST_GITHUB_USER_NAME");
      repoName = uid;
      _.gitSync.CreateTestGiteaRepo(repoName);
      //cy.createTestGithubRepo(repoName);

      // open gitSync modal
      cy.get(homePage.deployPopupOptionTrigger).click();
      cy.get(homePage.connectToGitBtn).click({
        force: true
      });
      cy.intercept({
        url: "api/v1/git/connect/*",
        hostname: window.location.host
      }, req => {
        req.headers["origin"] = "Cypress";
      });
      cy.intercept("POST", "/api/v1/applications/ssh-keypair/*").as(`generateKey-${repoName}`);
      cy.get(gitSyncLocators.gitRepoInput).type(`{selectAll}${datasourceFormData["GITEA_API_URL_TED"]}/${repoName}.git`);
      // abort git flow after generating key
      cy.get(gitSyncLocators.closeGitSyncModal).click();
    });
    // verify app is visible and open
    cy.NavigateToHome();
    cy.reload();
    cy.wait(3000);
    cy.SearchApp(`${newWorkspaceName}app`);
  });
});

