    _.agHelper.SetCanvasViewportWidth(375);
  }
  _.entityExplorer.DragDropWidgetNVerify(_.draggableWidgets.CONTAINER, 100, 30);
  _.agHelper.GetWidgetByName("Container1").invoke("attr", "id").then(id => {
    const dropTargetClass = `.drop-target-${id?.split("_")[1]}`;
    _.entityExplorer.DragDropWidgetNVerify("textwidget", 50, 20, dropTargetClass);

    // Add multi-line text & verify if the container's height increases
    _.agHelper.GetWidgetHeight(_.autoLayout._containerWidgetSelector).as("initialHeight");
    _.propPane.UpdatePropertyFieldValue("Text", "hello\nWorld\nThis\nis\na\nMulti-line\nText");
    _.agHelper.GetWidgetHeight(_.autoLayout._containerWidgetSelector).then(width => {
      cy.get < number > "@initialHeight".then(initialHeight => {
        expect(width).to.be.greaterThan(initialHeight);
      });
    });

    // Remove some lines & verify if the container's height decreases
    _.propPane.UpdatePropertyFieldValue("Text", "hello");
    _.agHelper.GetWidgetHeight(_.autoLayout._containerWidgetSelector).then(width => {
      cy.get < number > "@initialHeight".then(initialHeight => {
        expect(width).to.be.equal(initialHeight);
      });
    });
  });
});

