it("4. Should paste widgets in copied orientation, when multiple widgets are copied", () => {
  //Select and copy widgets in,
  // button in layer index 0, end aligned
  agHelper.GetNClick(locators._widgetInDeployed(draggableWidgets.BUTTON), 1, false, 500, true);
  // button in layer index 1, start aligned
  agHelper.GetNClick(locators._widgetInDeployed(draggableWidgets.BUTTON), 2, false, 500, true);
  // icon button in layer index 1, end aligned
  agHelper.GetNClick(locators._widgetInDeployed(draggableWidgets.ICONBUTTON), 0, false, 500, true);
  // button in layer index 2, center aligned
  agHelper.GetNClick(locators._widgetInDeployed(draggableWidgets.BUTTON), 3, false, 500, true);
  agHelper.AssertElementLength(locators._selectedWidget, 4);
  agHelper.GetElement("body").type(`{${modifierKey}}{c}`);
  agHelper.GetElement(locators._toastMsg).contains("Copied");

  //unselect all widgets
  agHelper.GetNClick(locators._selectionCanvas("0"), 0, true);
  agHelper.AssertElementLength(locators._selectedWidget, 0);
  //paste
  agHelper.GetElement("body").type(`{${modifierKey}}{v}`);
  agHelper.Sleep();

  //verify widgets paste in copied orientation,
  // button in layer index 2, end aligned
  autoLayout.VerifyIfChildWidgetPositionInFlexContainer(locators._appsmithWidget("0"), locators._widgetInDeployed(draggableWidgets.BUTTON), 2, "END");
  // button in layer index 3, start aligned
  autoLayout.VerifyIfChildWidgetPositionInFlexContainer(locators._appsmithWidget("0"), locators._widgetInDeployed(draggableWidgets.BUTTON), 3, "START");
  // icon button in layer index 3, center aligned
  autoLayout.VerifyIfChildWidgetPositionInFlexContainer(locators._appsmithWidget("0"), locators._widgetInDeployed(draggableWidgets.ICONBUTTON), 3, "CENTER");
  // button in layer index 4, center aligned
  autoLayout.VerifyIfChildWidgetPositionInFlexContainer(locators._appsmithWidget("0"), locators._widgetInDeployed(draggableWidgets.BUTTON), 4, "CENTER");

  //unselect all widgets
  agHelper.GetNClick(locators._selectionCanvas("0"), 0, true);
});

