it("2. Guided Tour", function () {
  // Start guided tour
  cy.get(commonlocators.homeIcon).click({
    force: true
  });
  cy.get(guidedTourLocators.welcomeTour).click();
  cy.get(guidedTourLocators.startBuilding).click();
  cy.get(explorerLocators.entityExplorer).should("not.be.visible");
  // Refresh the page to validate if the tour resumes
  cy.reload();
  cy.get(".query-page").then($ele => {
    if ($ele.find(guidedTourLocators.banner).length) {
      cy.get(guidedTourLocators.banner).should("be.visible");
    }
  });
  _.dataSources.SetQueryTimeout();
  // Step 1: Run query
  _.dataSources.RunQuery();
  cy.get(guidedTourLocators.successButton).click();
  // Step 2: Select table widget
  _.entityExplorer.SelectEntityByName("CustomersTable");

  // Step 3: Add binding to the tableData property
  _.propPane.UpdatePropertyFieldValue("Table data", "{{getCustomers.data}}");
  cy.get(guidedTourLocators.successButton).click();
  cy.get(guidedTourLocators.infoButton).click();
  // Renaming widgets // Commending below wait due to flakiness
  //cy.wait("@updateWidgetName");
  // Step 4: Add binding to the defaultText property of NameInput
  cy.wait(3000);
  cy.get("body").then($body => {
    if ($body.find(guidedTourLocators.hintButton).length > 0) {
      cy.get(guidedTourLocators.hintButton).click();
      cy.wait(1000); //for NameInput to open
      _.propPane.UpdatePropertyFieldValue("Default value", "{{CustomersTable.selectedRow.name}}");
    } else {
      cy.wait(1000);
      cy.get(guidedTourLocators.inputfields).first().clear({
        force: true
      }).click({
        force: true
      }); //Name input
      _.propPane.UpdatePropertyFieldValue("Default value", "{{CustomersTable.selectedRow.name}}");
    }
  });
  cy.get(guidedTourLocators.successButton).click();
  // Step 5: Add binding to the rest of the widgets in the container
  cy.get(guidedTourLocators.inputfields).eq(1).clear({
    force: true
  }).click({
    force: true
  }); //Email input
  _.propPane.UpdatePropertyFieldValue("Default value", "{{CustomersTable.selectedRow.email}}");
  cy.get(".t--entity-name").contains("CountryInput").click({
    force: true
  });
  cy.wait(1000);
  cy.get(guidedTourLocators.inputfields).eq(2).clear({
    force: true
  }).click({
    force: true
  }); //Country input
  _.propPane.UpdatePropertyFieldValue("Default value", "{{CustomersTable.selectedRow.country}}");
  cy.get(".t--entity-name").contains("DisplayImage").click({
    force: true
  });
  cy.get(guidedTourLocators.successButton).click();
  // Step 6: Drag and drop a widget
  cy.dragAndDropToCanvas("buttonwidget", {
    x: 800,
    y: 750
  });
  cy.get(guidedTourLocators.successButton).click();
  cy.get(guidedTourLocators.infoButton).click();
  // Step 7: Execute a query onClick
  cy.executeDbQuery("updateCustomerInfo", "onClick");
  // Step 8: Execute getCustomers onSuccess
  _.propPane.SelectActionByTitleAndValue("Execute a query", "updateCustomerInfo.run"), cy.get(_.propPane._actionCallbacks).first().click();
  cy.get(_.propPane._actionAddCallback("success")).click().wait(500);
  cy.get(_.locators._dropDownValue("Execute a query")).click().wait(500).get("ul.bp3-menu").children().contains("getCustomers").click({
    force: true
  }).wait(500);
  _.agHelper.GetNClick(_.propPane._actionSelectorPopupClose);
  cy.get(guidedTourLocators.successButton).click();
  // Step 9: Deploy
  cy.PublishtheApp();
  cy.get(guidedTourLocators.rating).should("be.visible");
  cy.get(guidedTourLocators.rating).eq(4).click();
  cy.get(guidedTourLocators.startBuilding).should("be.visible");
  cy.get(guidedTourLocators.startBuilding).click();
});

