it("excludeForAirgap", "1. Test case while reloading and on submission", function () {
  /**
   * Use case:
   * 1. Open Datasource editor
   * 2. Click on sample(mock) user database.
   * 3. Choose the first data source which consists of users keyword
   * 4. Click on the "New query +"" button
   * 5. Name the Query as "Query1"
   * 6. Write the query as "SELECT * FROM users ORDER BY id LIMIT 10;"
   * 7. Run the Query
   * 8. Go to Widgets
   * 9. Click on the Submit Button
   * 10. Check if we get "bp3-skeleton" class
   * 11. Reload the page
   * 12. Do step 10.
   * 13. Remove the datasource*
   */

  //Step1
  cy.wait(2000);
  _.dataSources.CreateMockDB("Users").then(dbName => {
    _.dataSources.CreateQueryFromActiveTab(dbName, false);
    _.agHelper.GetNClick(_.dataSources._templateMenuOption("Select"));
    _.dataSources.ToggleUsePreparedStatement(false);
  });

  // Step6.2: writing query to get the schema
  cy.get(".CodeMirror textarea").first().focus().type("SELECT * FROM users ORDER BY id LIMIT 10;", {
    force: true,
    parseSpecialCharSequences: false
  });
  cy.WaitAutoSave();

  //Step7:
  cy.runQuery();

  //Step8:
  cy.get('.t--entity-name:contains("Page1")').click({
    force: true
  });
  cy.wait(1000);

  //Step9:
  cy.get(".bp3-button-text").first().click({
    force: true
