it("2. Create Post echo Api call", function () {
  cy.NavigateToAPI_Panel();
  cy.CreateAPI("send_mail");
  cy.get(apiEditor.ApiVerb).click();
  cy.xpath(appPage.selectPost).click(); // creating post request using echo

  cy.enterDatasourceAndPath("https://mock-api.appsmith.com/echo", "/post");
  cy.contains(apiEditor.bodyTab).click({
    force: true
  });
  cy.xpath(apiwidget.postbody).click({
    force: true
  }).clear(); // binding the data with widgets in body tab

  cy.xpath(apiwidget.postbody).click({
    force: true
  }).focus().type('{"to":"{{to_input.text}}","subject":"{{subject.text}}","content":"{{content.text}}"}', {
    parseSpecialCharSequences: false
  }).type("{del}{del}{del}");
  cy.SaveAndRunAPI();
  cy.ResponseStatusCheck("201");
});

