it("Check the url of old applications, upgrades version and compares appsmith.URL values", () => {
  cy.request("PUT", `/api/v1/applications/${applicationId}`, {
    applicationVersion: 1
  }).then(response => {
    const application = response.body.data;
    expect(application.applicationVersion).to.equal(1);
    cy.NavigateToHome();
    cy.reload();
    cy.SearchApp(applicationName);
    cy.wait("@getPagesForCreateApp").then(intercept => {
      const {
        application,
        pages
      } = intercept.response.body.data;
      const defaultPage = pages.find(p => p.isDefault);
      cy.location().should(loc => {
        expect(loc.pathname).includes(`/applications/${application.id}/pages/${defaultPage.id}`);
      });
      cy.Createpage("NewPage");
      cy.get("@currentPageId").then(currentPageId => {
        cy.location().should(loc => {
          expect(loc.pathname).includes(`/applications/${application.id}/pages/${currentPageId}`);
        });
        cy.get(explorer.addWidget).click();
        cy.dragAndDropToCanvas("textwidget", {
          x: 300,
          y: 700
        });
        cy.get(".t--widget-textwidget").should("exist");
        cy.updateCodeInput(".t--property-control-text", `{{appsmith.URL.pathname}}`);
        cy.get(".t--draggable-textwidget .bp3-ui-text").should("contain.text", `/applications/${application.id}/pages/${currentPageId}/edit`);
        cy.get(".t--upgrade").click({
          force: true
        });
        cy.get(".t--upgrade-confirm").click({
          force: true
        });
        cy.wait("@getPagesForCreateApp").then(intercept => {
          const {
            application,
            pages
          } = intercept.response.body.data;
          const currentPage = pages.find(p => p.id === currentPageId);
          cy.location().should(loc => {
            expect(loc.pathname).includes(`/app/${application.slug}/${currentPage.slug}-${currentPage.id}`);
          });
          cy.get(".t--draggable-textwidget .bp3-ui-text").should("contain.text", `/app/${application.slug}/${currentPage.slug}-${currentPage.id}/edit`);
          cy.visit(`/${application.slug}/${currentPage.slug}-${currentPage.id}/edit`);
          cy.location().should(loc => {
            expect(loc.pathname).includes(`/app/${application.slug}/${currentPage.slug}-${currentPage.id}/edit`);
          });
        });
      });
    });
  });
});

