  cy.get(".t--property-control-fixedfooter").find("label.bp3-control").should("have.class", "checked"); // Check if there is a gap between body and footer

  cy.get(".t--jsonform-body").then($body => {
    cy.get(".t--jsonform-footer").then($footer => {
      const gap = $footer.prop("offsetTop") - $body.prop("scrollHeight");
      expect(gap).greaterThan(0);
    });
  });
});

