it("5. Verify that js function execution errors are logged in debugger and removed when function is deleted", () => {
  const JS_OBJECT_WITH_PARSE_ERROR = `export default {
    myVar1: [],
    myVar2: {},
    myFun1: () => {
      //write code here
      return Table1.unknown.name
    }
  }`;
  const JS_OBJECT_WITHOUT_PARSE_ERROR = `export default {
    myVar1: [],
    myVar2: {},
    myFun1: () => {
      //write code here
      return Table1.unknown
    }
  }`;
  const JS_OBJECT_WITH_DELETED_FUNCTION = `export default {
    myVar1: [],
    myVar2: {}
  }`; // Create js object

  jsEditor.CreateJSObject(JS_OBJECT_WITH_PARSE_ERROR, {
    paste: true,
    completeReplace: true,
    toRun: true,
    shouldCreateNewJSObj: true
  }); // Assert that there is a function execution parse error

  jsEditor.AssertParseError(true, true); // click the debug icon

  agHelper.GetNClick(jsEditor._debugCTA); // Assert that errors tab is not empty

  cy.contains("No signs of trouble here!").should("not.exist"); // Assert presence of typeError

  cy.contains("TypeError: Cannot read properties of undefined (reading 'name')").should("exist"); // Fix parse error and assert that debugger error is removed

  jsEditor.EditJSObj(JS_OBJECT_WITHOUT_PARSE_ERROR);
  agHelper.GetNClick(jsEditor._runButton);
  jsEditor.AssertParseError(false, true);
  agHelper.GetNClick(locator._errorTab);
  cy.contains("TypeError: Cannot read properties of undefined (reading 'name')").should("not.exist"); // Switch back to response tab

  agHelper.GetNClick(locator._responseTab); // Re-introduce parse errors

  jsEditor.EditJSObj(JS_OBJECT_WITH_PARSE_ERROR);
  agHelper.GetNClick(jsEditor._runButton);
  agHelper.WaitUntilToastDisappear("ran successfully"); //to not hinder with next toast msg in next case!
  // Assert that there is a function execution parse error

  jsEditor.AssertParseError(true, true); // Delete function

  jsEditor.EditJSObj(JS_OBJECT_WITH_DELETED_FUNCTION); // Assert that parse error is removed from debugger when function is deleted

  agHelper.GetNClick(locator._errorTab);
  cy.contains("TypeError: Cannot read properties of undefined (reading 'name')").should("not.exist");
});

