it("3. Will pass execution params", function () {
  // Bind the table
  cy.SearchEntityandOpen("Table1");
  cy.testJsontext("tabledata", "{{Query1.data}}"); // Assert 'posts' data (default)

  cy.readTabledataPublish("0", "1").then(cellData => {
    expect(cellData).to.be.equal("Test user 7");
  }); // Choose static button

  cy.SearchEntityandOpen("StaticButton"); // toggle js of onClick

  cy.get(".t--property-control-onclick").find(".t--js-toggle").click({
    force: true
  }); // Bind with MultiApi with static value

  cy.testJsontext("onclick", "{{Query1.run(undefined, undefined, { tableName: 'users' })}}"); // Choose dynamic button

  cy.SearchEntityandOpen("DynamicButton");
  cy.wait(2000); // toggle js of onClick

  cy.get(".t--property-control-onclick").scrollIntoView();
  cy.get(".t--property-control-onclick").find(".t--js-toggle").click({
    force: true
  }); // Bind with MultiApi with dynamicValue value

  cy.testJsontext("onclick", "{{Query1.run(undefined, undefined, { tableName: EndpointInput.text })}}"); // Publish the app

  cy.PublishtheApp(); // Assert on load data in table

  cy.readTabledataPublish("0", "1").then(cellData => {
    expect(cellData).to.be.equal("Test user 7");
  }); // Click Static button

  cy.get(publishPage.buttonWidget).first().click(); //Wait for postExecute to finish

  cy.wait("@postExecute").should("have.nested.property", "response.body.responseMeta.status", 200); // eslint-disable-next-line cypress/no-unnecessary-waiting

  cy.wait(5000); // Assert statically bound "users" data

  cy.readTabledataPublish("1", "1").then(cellData => {
    expect(cellData).to.be.equal("Test user 8");
  }); // Click dynamic button

  cy.get(publishPage.buttonWidget).eq(1).click(); //Wait for postExecute to finish

  cy.wait("@postExecute").should("have.nested.property", "response.body.responseMeta.status", 200); // eslint-disable-next-line cypress/no-unnecessary-waiting

  cy.wait(5000); // Assert dynamically bound "todos" data

  cy.readTabledataPublish("0", "1").then(cellData => {
    expect(cellData).to.be.equal("Test user 7");
  });
});

