  cy.wait("@postEnvVariables").then(interception => {
    expect(interception.request.body.APPSMITH_INSTANCE_NAME).to.equal(instanceName);
    expect(interception.request.body.APPSMITH_MAIL_FROM).to.equal(`${fromAddress}@appsmith.com`);
  });
