it("Change the cell background color", function () {
  cy.openPropertyPane("textwidget");
  /**
   * @param{Text} Random Text
   * @param{CheckboxWidget}Mouseover
   * @param{CheckboxPre Css} Assertion
   */
  //Check if the cell background is #03b365

  cy.selectColor("cellbackground");
  cy.get(`${widgetsPage.textWidget} .bp3-ui-text`).should("have.css", "background-color", "rgb(3, 179, 101)"); //Toggle to JS mode

  cy.get(widgetsPage.toggleJsBcgColor).click().wait(200); //Check if the typed color red is reflecting in the background color and in the evaluated value

  cy.updateCodeInput(".t--property-control-cellbackground", "red");
  cy.get(`${widgetsPage.textWidget} .bp3-ui-text`).should("have.css", "background-color", "rgb(255, 0, 0)");
  cy.EvaluateCurrentValue("red"); //Check if the typed color #03b365 is reflecting in the background color and in the evaluated value

  cy.updateCodeInput(".t--property-control-cellbackground", "#03b365");
  cy.get(`${widgetsPage.textWidget} .bp3-ui-text`).should("have.css", "background-color", "rgb(3, 179, 101)");
  cy.EvaluateCurrentValue("#03b365"); //Check if the typed color transparent is reflecting in the background color and in the evaluated value

  cy.updateCodeInput(".t--property-control-cellbackground", "");
  cy.get(`${widgetsPage.textWidget} .bp3-ui-text`).should("have.css", "background-color", "rgba(0, 0, 0, 0)");
  cy.get(commonlocators.evaluatedCurrentValue).first().should("not.be.visible");
});

