test('Rules mixed with media queries', () => {
  var m1 = comp.get('components').add({
    tagName: 'article'
  });
  var cls1 = m1.get('classes').add({
    name: 'class1'
  });
  var cls2 = m1.get('classes').add({
    name: 'class2'
  });
  var cssc = newCssComp();
  var rule = cssc.add([cls1, cls2]);
  rule.set('style', {
    prop1: 'value1'
  });
  var rule2 = cssc.add(cls2);
  rule2.set('style', {
    prop2: 'value2'
  });
  var rule3 = cssc.add(cls1, '', '(max-width: 999px)');
  rule3.set('style', {
    prop3: 'value3'
  });
  var rule4 = cssc.add(cls2, '', '(max-width: 999px)');
  rule4.set('style', {
    prop4: 'value4'
  });
  var rule5 = cssc.add(cls1, '', '(max-width: 100px)');
  rule5.set('style', {
    prop5: 'value5'
  });
  expect(obj.build(comp, {
    cssc
  })).toEqual('.class1.class2{prop1:value1;}.class2{prop2:value2;}' + '@media (max-width: 999px){.class1{prop3:value3;}.class2{prop4:value4;}}' + '@media (max-width: 100px){.class1{prop5:value5;}}');
});

