  all.forEach(cmp => expect(cmp.components().length).toBe(compLen));
  expect(wrapper.components().length).toBe(4); // Append new component to one of the instances

  const added = comp3.append(simpleComp, {
    at: 0
  })[0]; // The append should be propagated

  all.forEach(cmp => expect(cmp.components().length).toBe(compLen + 1)); // The new added component became part of the symbol instance

  const addedSymb = added.__getSymbol();

  const symbAdded = symbol.components().at(0);
  expect(addedSymb).toBe(symbAdded);
  allInst.forEach(cmp => expect(cmp.components().at(0).__getSymbol()).toBe(symbAdded)); // The new main Symbol should keep the track of all instances

  expect(symbAdded.__getSymbols().length).toBe(allInst.length);
});

