test('Symbol with override returns correctly instances to update', () => {
  expect(symbol.__getSymbToUp().length).toBe(allInst.length); // With override as `true`, it will return empty array with any 'changed'

  symbol.set(keySymbolOvrd, true);
  expect(symbol.__getSymbToUp({
    changed: 'anything'
  }).length).toBe(0); // With override as an array with props, changed option will count

  symbol.set(keySymbolOvrd, ['components']);
  expect(symbol.__getSymbToUp({
    changed: 'anything'
  }).length).toBe(allInst.length);
  symbol.set(keySymbolOvrd, ['components']);
  expect(symbol.__getSymbToUp({
    changed: 'components'
  }).length).toBe(0);
  expect(symbol.__getSymbToUp({
    changed: 'components:reset'
  }).length).toBe(0); // Support also overrides with type of actions

  symbol.set(keySymbolOvrd, ['components:change']); // specific change

  expect(symbol.__getSymbToUp({
    changed: 'components'
  }).length).toBe(allInst.length);
  expect(symbol.__getSymbToUp({
    changed: 'components:change'
  }).length).toBe(0);
});

test('Symbol is not propagating props data if override is set', () => {
  const propKey = 'someprop';
  const propValue = 'somevalue';
  symbol.set(keySymbolOvrd, true); // Single prop update

  symbol.set(propKey, propValue);
  allInst.forEach(cmp => expect(cmp.get(propKey)).toBeFalsy()); // Multiple props

  symbol.set({
    prop1: 'value1',
    prop2: 'value2'
  });
  allInst.forEach(cmp => {
    expect(cmp.get('prop1')).toBeFalsy();
    expect(cmp.get('prop2')).toBeFalsy();
  }); // Override applied on specific properties

  symbol.set(keySymbolOvrd, ['prop1']);
  symbol.set({
    prop1: 'value1-2',
    prop2: 'value2-2'
  });
  allInst.forEach(cmp => {
    expect(cmp.get('prop1')).toBeFalsy();
    expect(cmp.get('prop2')).toBe('value2-2');
  });
});

test('On symbol props update, those having override are ignored', () => {
  const propKey = 'someprop';
  const propValue = 'somevalue';
  comp.set(keySymbolOvrd, true);
  symbol.set(propKey, propValue); // All symbols are updated except the one with override

  all.forEach(cmp => {
    if (cmp === comp) {
      expect(cmp.get(propKey)).toBeFalsy();
    } else {
      expect(cmp.get(propKey)).toBe(propValue);
    }
  });
  comp.set(keySymbolOvrd, ['prop1']);
  symbol.set({
    prop1: 'value1',
    prop2: 'value2'
  }); // Only the overrided property is ignored

  all.forEach(cmp => {
    if (cmp === comp) {
      expect(cmp.get('prop1')).toBeFalsy();
      expect(cmp.get('prop2')).toBe('value2');
    } else {
      expect(cmp.get('prop1')).toBe('value1');
      expect(cmp.get('prop2')).toBe('value2');
    }
  });
});

test('Symbol is not propagating components data if override is set', () => {
