test('dimsFromTarget', () => {
  var child1 = document.createElement('div');
  var child2 = document.createElement('div');
  child1.style.width = '30px';
  child1.style.height = '30px';
  child2.style.width = '30px';
  child2.style.height = '20px';
  sib3.appendChild(child1);
  sib3.appendChild(child2);
  var subPos = obj.offset(sib1);
  var top = subPos.top;
  var left = subPos.left;
  var topSib3 = top + 100;
  var leftSib3 = left + 0;
  var resultParent = [[top, left, 50, 100, true], [top + 50, left + 0, 50, 100, true], [topSib3, leftSib3, 50, 100, true], [top + 100, left + 100, 50, 70, true]];
  var resultChildren = [[topSib3, leftSib3, 30, 30, true], [topSib3 + 30, left + 0, 20, 30, true]];
  var dims = obj.dimsFromTarget(sib3);
  dims = dims.map(function (v) {
    return v.slice(0, 5);
  });
  expect(dims).toEqual(resultParent); // Inside target

  var dims = obj.dimsFromTarget(sib3, leftSib3 + 15, topSib3 + 15);
  dims = dims.map(function (v) {
    return v.slice(0, 5);
  });
  expect(dims).toEqual(resultChildren); // Exactly on border

  var bOffset = obj.borderOffset;
  var dims = obj.dimsFromTarget(sib3, leftSib3 + bOffset, topSib3 + bOffset);
  dims = dims.map(function (v) {
    return v.slice(0, 5);
  });
  expect(dims).toEqual(resultChildren); // Slightly near border

  var dims = obj.dimsFromTarget(sib3, leftSib3 + bOffset - 3, topSib3 + bOffset);
  dims = dims.map(function (v) {
    return v.slice(0, 5);
  });
  expect(dims).toEqual(resultParent);
});

