  spyOn(atom, 'serialize').andReturn({
    stuff: 'cool'
  });
  atom.project.setPaths([dir1, dir2]);

  // State persistence will fail if other Atom instances are running
  expect(await atom.stateStore.connect()).toBe(true);
  await atom.saveState();
