    bar: {
      baz: 10
    }
  });
  atom.config.set('foo.bar.baz', 42, {
    scopeSelector: '.source.coffee',
    source: 'some-source'
  });
  atom.config.set('foo.bar.baz', 55, {
    scopeSelector: '.source.coffee'
  });
  atom.config.set('foo.bar.ok', 100, {
    scopeSelector: '.source.coffee'
  });
  expect(atom.config.get('foo.bar.baz', {
    scope: ['.source.coffee']
  })).toBe(55);
  atom.config.unset('foo.bar.baz', {
    scopeSelector: '.source.coffee'
  });
  expect(atom.config.get('foo.bar.baz', {
    scope: ['.source.coffee']
  })).toBe(42);
  expect(atom.config.get('foo.bar.ok', {
    scope: ['.source.coffee']
  })).toBe(100);
});

